import React, { useRef, useState, useEffect } from "react";
import useMovies from "../api/movies";
import "../styles/banner-home.css";

import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";


const BannerHomePage = () => {
  const { movies } = useMovies();
  const [listMovie, setListMovie] = useState([]);
  useEffect(() => {
    if (movies.length > 0) {
      setListMovie(movies);
    }
  }, [movies]);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true
  };

  return (
    <section className="movies">
      <nav>
        <Slider className="slider-nav">
         {
            listMovie.slice(0,4).map((movie, index) =>{
                return <li key={index}><a href="#">{movie.name}</a></li>
            }
         )} 
        </Slider>
      </nav>
      <div className="banner slider-for">
        
        {
            listMovie.slice(0,4).map((movie, index) =>{
              return(
                <div className="banner-slide" key={index}>
                    <div className="banner-inner">
                        <div className="banner-desc">
                          <h2 className="movieName">{movie.name}</h2>
                          <h1 className="authorName">{movie.description}</h1>
                        </div>
                        <img
                          src={`https://phimhay2402.vercel.app/${movie.imgBanner}`}
                          alt="automobile-3-image"
                        />
                    </div>
                </div>
                )
            })
        }
      </div>
    </section>
  );
};

export default BannerHomePage;
