// src/components/AddMovie.js
import React, { useState } from 'react';
import axios from 'axios';

const AddMovie = () => {
  const [title, setTitle] = useState('');
  const [year, setYear] = useState('');
  const [description, setDescription] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [bannerFile, setBannerFile] = useState('');
  const handleAddMovie =  async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append('file', bannerFile);
    formData.append('title', title);
    formData.append('year', year);
    formData.append('description', description);
    axios.post('http://localhost:5000/api/movies', { title, year, description, videoUrl })
      .then(response => {
        console.log('Movie added:', response.data);
      })
      .catch(error => {
        console.error('Error adding movie:', error);
      });
  };

  return (
    <div>
      <h2>Add Movie</h2>
      <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
      <input type="number" placeholder="Year" value={year} onChange={e => setYear(e.target.value)} />
      <input type="text" placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} />
      <input type="text" placeholder="Video URL" value={videoUrl} onChange={e => setVideoUrl(e.target.value)} />
      <input type="file" placeholder="Banner URL" value={bannerFile} onChange={e => setBannerFile(e.target.files[0])} />
      <button onClick={handleAddMovie}>Add Movie</button>
    </div>
  );
};

export default AddMovie;
