import React, { useState, useRef, useEffect } from "react";
import "../styles/Carousel.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import useMovies from "../api/movies";

const Carousel = () => {
  
  const { movies } = useMovies();

  const [currentSlides, setCurrentSlides] = useState([]);

    useEffect(() => {
        if (movies.length > 0) {
            setCurrentSlides(movies.slice(0, 10)); // Hiển thị 10 phim đầu tiên
        }
    }, [movies]);

  const nextSlide = () => {
    // setCurrentSlides((prevSlides) => {
    //   const firstSlide = prevSlides.shift();
    //   return [...prevSlides, firstSlide];
    // });
  };

  const prevSlide = () => {
    // setCurrentSlides((prevSlides) => {
    //   const lastSlide = prevSlides.pop();
    //   return [lastSlide, ...prevSlides];
    // });
  };

  return (
    <>
      <div className="carousel-container">
        <div>
          <h5>Top 10 movies to watch</h5>
        </div>
        <div className="slide">
          <div className="prevSlide">
            <button onClick={prevSlide}>
              <FontAwesomeIcon
                icon="fa-solid fa-chevron-left "
                className="icon"
                size="2xl"
              />
            </button>
          </div>
          <div className="slide-carousel">
            {currentSlides.map((slide, index) => {
              return (
                <div className="slide carousel" key={index}>
                  <img src={`http://localhost:5000${slide.imgBanner}`} alt="" />
                  <div className="content">
                    <span>{index+1}</span>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="nextSlide">
            <button onClick={nextSlide}>
              <FontAwesomeIcon
                icon="fa-solid fa-angle-right"
                className="icon"
                size="2xl"
              />
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Carousel;
