import React, { useRef, useState, useEffect } from "react";
import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import "../styles/home.css";



import axios from "axios";

import Carousel from "../component/Carousel ";
library.add(fas);

function HomePage() {


  const [movies, setMovies] = useState([]);
  

  // lấy Api movies
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/movies/");
        setMovies(response.data);
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    };

    fetchMovies();
  }, []);
  // end Api movies

  return (
    <>
      <div className="carousel">
        <div className="list">
          {movies.map((movie, index) => (
            <div className="item" key={index}>
              <img
                src={`http://localhost:5000${movie.imgBanner}`}
                alt={`Slide ${index + 1}`}
              />
              <div className="content">
                <div className="title">{movie.description}</div>
                <div className="topic">{movie.year}</div>
                <div className="des">
                  {/* {directorName[0]} - {movieDescription[0]} */}
                </div>
                <div className="buttons">
                  <button>
                    <a href={movie.videoUrl}>SEE MORE</a>
                  </button>
                  <button>SUBSCRIBE</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="thumbnail">
          {movies.map((movie, index) => (
            <div className="item" key={index}>
              <img
                src={`http://localhost:5000${movie.imgAvata}`}
                alt={`Thumbnail ${index + 1}`}
              />
              <div className="content">
                <div className="title">Name Slider</div>
                <div className="description">Description</div>
              </div>
            </div>
          ))}
        </div>

        {/* <div className="arrows">
          <button ref={prevRef}>Previous</button>
          <button ref={nextRef}>Next</button>
        </div> */}
      </div>
      {/* <div className="overlay"></div> */}
      <Carousel />
    </>
  );
}

export default HomePage;
