import React, { useState, useRef, useEffect } from "react";
import "../styles/Carousel.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import img1 from "../img/r-01.webp";
import img2 from "../img/r-02.webp";
import img3 from "../img/r-03.webp";
import img4 from "../img/r-04.webp";
import img5 from "../img/r-05.webp";
import img6 from "../img/r-06.webp";
import img7 from "../img/r-07.webp";
import axios from "axios";

const slides = [
  { id: 1, image: img1 },
  { id: 2, image: img2 },
  { id: 3, image: img3 },
  { id: 4, image: img4 },
  { id: 5, image: img5 },
  { id: 6, image: img6 },
  { id: 7, image: img7 },
  { id: 8, image: img3 },
  { id: 9, image: img5 },
  {
    id: 10,
    image: "img10.jpg",
    title: "Slide 10",
    description: "Description 10",
  },
];



const Carousel = () => {
const [movies, setMovies] = useState([]);
  // lấy Api movies
useEffect(() => {
  const fetchMovies = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/movies/");
      setMovies(response.data);
    } catch (error) {
      console.error("Error fetching movies:", error);
    }
  };

  fetchMovies();
}, []);
// end Api movies

  const [currentSlides, setCurrentSlides] = useState(movies.slice(0, 10));

  const nextSlide = () => {
    setCurrentSlides((prevSlides) => {
      const firstSlide = prevSlides.shift();
      return [...prevSlides, firstSlide];
    });
  };

  const prevSlide = () => {
    setCurrentSlides((prevSlides) => {
      const lastSlide = prevSlides.pop();
      return [lastSlide, ...prevSlides];
    });
  };

  return (
    <>
      <div className="carousel-container">
        <div>
          <h5>Top 10 movies to watch</h5>
        </div>
        <div className="slide">
          <div className="prevSlide">
            <button onClick={prevSlide}><FontAwesomeIcon icon="fa-solid fa-chevron-left " className="icon" size="2xl"/></button>
          </div>
          <div className="slide-carousel">
            {currentSlides.map((slide, index) => (
              <div className="slide" key={index}>
                <img src={`http://localhost:5000${slide.imgBanner}`} alt="" />
                <div className="content">
                  <span>{slide.id}</span>
                </div>
              </div>
            ))}
          </div>
          <div className="nextSlide">
            <button onClick={nextSlide}><FontAwesomeIcon icon="fa-solid fa-angle-right" className="icon" size="2xl"/></button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Carousel;
