import { library } from '@fortawesome/fontawesome-svg-core';
import { fas } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
library.add(fas);

function HomePage() {
  return (
    <div className="banner">
      <div className="content">
        <img className="movie-title" alt=""></img>
        <h4>
          <span>2024</span>
          <span>
            <i>12+</i>
          </span>
          <span>2h 14min</span>
          <span>Romance</span>
        </h4>
        <p>nội dung tại đây</p>
        <div className="button">
            <a href="#"><FontAwesomeIcon icon="fa-solid fa-play" size="xl" />Play</a>
            <a href="#"><FontAwesomeIcon icon="fa-solid fa-plus" size="xl" />Watch Trailer</a>
        </div>
      </div>
    </div>
  );
}
export default HomePage;
