import React, { useRef, useState, useEffect } from "react";
import {
  faChevronLeft,
  faChevronRight,
} from "@fortawesome/free-solid-svg-icons";
import "../styles/home.css";

import axios from "axios";

import Carousel from "../component/Carousel ";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function HomePage() {
  const [movies, setMovies] = useState([]);

  // lấy Api movies
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/movies/");
        setMovies(response.data);
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    };

    fetchMovies();
  }, []);
  // end Api movies

  // Chuyển động slide
  const [currentIndex, setCurrentIndex] = useState(0);
  const slideRef = useRef(null);
  const nextRef = useRef(null);
  const prevRef = useRef(null);

  useEffect(() => {
    const slide = slideRef.current;
    const next = nextRef.current;
    const prev = prevRef.current;
    let autoplay = 5000; // milliseconds

    const moveToNextSlide = () => {
      let items = slide.querySelectorAll(".item");
      slide.appendChild(items[0]);
      autoplay = 5000;
      setCurrentIndex((currentIndex + 1) % movies.length);
    };

    const moveToPrevSlide = () => {
      let items = slide.querySelectorAll(".item");
      slide.prepend(items[items.length - 1]);
      autoplay = 5000;
      setCurrentIndex((currentIndex - 1 + movies.length) % movies.length);
    };

    next.addEventListener("click", moveToNextSlide);
    prev.addEventListener("click", moveToPrevSlide);

    const intervalId = setInterval(() => {
      moveToNextSlide();
    }, autoplay);


    // Clean up event listeners on component unmount
    return () => {
      next.removeEventListener("click", moveToNextSlide);
      prev.removeEventListener("click", moveToPrevSlide);
      clearInterval(intervalId);
    };
  }, []);

  return (
    <>
      <div className="container-fluid gx-0 slide-container">
        <div id="slide" ref={slideRef}>
          {movies.slice(0,6).map((movie, index) => {
            return (
              <div
                key={index}
                className={`item ${index === currentIndex ? "active" : ""}`}
                style={{
                  backgroundImage: `url(http://localhost:5000${movie.imgBanner})`,
                }}
              >
                <div className="content">
                  <h3>{movie.name}</h3>
                  <p>{movie.description.substring(0, 100)}...</p>
                  <button>
                    <a href={movie.videoUrl}>Xem phim</a>
                  </button>
                </div>
              </div>
            );
          })}
        </div>
        <div className="buttons">
          <button className="btn btn-previous" ref={prevRef}>
          <FontAwesomeIcon icon={faChevronLeft} />
           
          </button>
          <button className="btn btn-next"  ref={nextRef}>
          <FontAwesomeIcon icon={faChevronRight} />
          </button>
        </div>
      </div>
      <Carousel />
    </>
  );
}

export default HomePage;
