import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import React, { useEffect } from "react";
import "../styles/home.css";
import "materialize-css/dist/css/materialize.min.css";
import "materialize-css/dist/js/materialize.min.js";
import M from "materialize-css";

import ott1 from '../img/ott1.webp';
import ott2 from '../img/ott2.webp';
import ott3 from '../img/ott3.webp';
library.add(fas);

function HomePage() {
  useEffect(() => {
    const elems = document.querySelectorAll('.carousel');
    console.log(elems.length);
    M.Carousel.init(elems, {
      duration: 300,
      dist: -50,
      shift: 10,
      padding: 20,
      numVisible: 3,
      fullWidth: true,
      indicators: true,
      noWrap: false
    });
  }, []);
  return (
    <div className="banner">
      <div className="content active">
        <img className="movie-title" alt=""></img>
        <h4>The Mandalorian</h4>
        <p>
          After the fall of the Galactic Empire, a lone gunfighter makes his way
          through the outer reaches of the lawless galaxy he show follows the
          adventures of a lone Mandalorian bounty hunter, Din Djarin, as he
          navigates the outer reaches of the galaxy, far from the authority of
          the New Republic.{" "}
        </p>
        <div className="button">
          <Link to="">
            <FontAwesomeIcon
              icon="fa-solid fa-play"
              size="xl"
              className="icon"
            />
            Play
          </Link>
          <Link to="">
            <FontAwesomeIcon
              icon="fa-solid fa-plus"
              size="xl"
              className="icon"
            />
            Watch Trailer
          </Link>
        </div>
      </div>
      <div className="carousel-box">
        {/* <div className="carousel">
          <div className="carousel-item">
            <img src={ott1} alt="Carousel Item 1"/>
          </div>
          <div className="carousel-item">
            <img src={ott2} alt="Carousel Item 2"/>
          </div>
          <div className="carousel-item">
            <img src={ott1} alt="Carousel Item 3"/>
          </div>
          <div className="carousel-item">
            <img src={ott3} alt="Carousel Item "/>
          </div>
        </div> */}
         <div className="carousel">
            <a className="carousel-item" href="#one!"><img src="https://lorempixel.com/250/250/nature/1" alt="Nature 1" /></a>
            <a className="carousel-item" href="#two!"><img src="https://lorempixel.com/250/250/nature/2" alt="Nature 2" /></a>
            <a className="carousel-item" href="#three!"><img src="https://lorempixel.com/250/250/nature/3" alt="Nature 3" /></a>
            <a className="carousel-item" href="#four!"><img src="https://lorempixel.com/250/250/nature/4" alt="Nature 4" /></a>
            <a className="carousel-item" href="#five!"><img src="https://lorempixel.com/250/250/nature/5" alt="Nature 5" /></a>
        </div>
      </div>
    </div>
  );
}
export default HomePage;
