import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import M from "materialize-css";
import React, { useEffect } from "react";
import '../img/ott1.webp'
import "../styles/home.css";
import "materialize-css/dist/css/materialize.min.css";
import "materialize-css/dist/js/materialize.min.js";

library.add(fas);

function HomePage() {
  useEffect(() => {
    const elems = document.querySelectorAll(".carousel");
    M.Carousel.init(elems, {
      duration: 200,
      dist: -80,
      shift: 3,
      padding: 5,
      numVisible: 3,
      indicators: true,
      noWrap: false,
    });
  }, []);
  return (
    <div className="banner">
      <div className="content active">
        <img className="movie-title" alt=""></img>
        <h4>The Mandalorian</h4>
        <p>
          After the fall of the Galactic Empire, a lone gunfighter makes his way
          through the outer reaches of the lawless galaxy he show follows the
          adventures of a lone Mandalorian bounty hunter, Din Djarin, as he
          navigates the outer reaches of the galaxy, far from the authority of
          the New Republic.{" "}
        </p>
        <div className="button">
          <Link to="">
            <FontAwesomeIcon
              icon="fa-solid fa-play"
              size="xl"
              className="icon"
            />
            Play
          </Link>
          <Link to="">
            <FontAwesomeIcon
              icon="fa-solid fa-plus"
              size="xl"
              className="icon"
            />
            Watch Trailer
          </Link>
        </div>
      </div>
      <div className="carousel-box">
        <div className="carousel">
          <div className="carousel-item">
            <img src='ott1.webp' alt="Carousel Item 1"></img>
          </div>
          <div className="carousel-item">
            <img src="../img/ott1.webp" alt="Carousel Item 2"></img>
          </div>
          <div className="carousel-item">
            <img src="../img/ott2.webp" alt="Carousel Item 3"></img>
          </div>
          <div className="carousel-item">
            <img src="../img/ott3.webp" alt="Carousel Item 4"></img>
          </div>
        </div>
      </div>
    </div>
  );
}
export default HomePage;
