import React, { useRef, useState, useEffect } from "react";
import useMovies from "../api/movies";

const BannerHomePage = () => {
  const { movies } = useMovies();
  const [listMovie, setListMovie] = useState([]);
  useEffect(() => {
    if (movies.length > 0) {
      setListMovie(movies);
    }
  }, [movies]);
  return (
    <section class="movies">
      <nav>
        <ul class="slider-nav">
         {
            listMovie.slice(0,4).map((movie, index) =>{
                return <li key={index}><a href="#">{movie.name}</a></li>
            }
         )} 
        </ul>
      </nav>
      <div class="banner slider-for">
        {/* <div class="banner-slide">
          <div class="banner-inner">
            <div class="banner-desc">
              <h2>lamborgini</h2>
              <h1>gallardo</h1>
            </div>
            <img
              src="https://www.yudiz.com/codepen/automobile-hero-slider/gallardo.png"
              alt="automobile-3-image"
            />
          </div>
        </div> */}
        {
            listMovie.slice(0,4).map((movie, index) =>{
                <div class="banner-slide">
                    <div class="banner-inner">
                        <div class="banner-desc">
                          <h2 className="movieName">{movie.name}</h2>
                          <h1 className="authorName">{movie.description}</h1>
                        </div>
                        <img
                          src={`https://phimhay2402.vercel.app/${movie.imgBanner}`}
                          alt="automobile-3-image"
                        />
                    </div>
                </div>
            })
        }
      </div>
    </section>
  );
};

export default BannerHomePage;
