import React, { useRef, useState, useEffect } from "react";
import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import "../styles/home.css";

import ott1 from "../img/ott1.webp";
import ott2 from "../img/ott2.webp";
import ott3 from "../img/ott3.webp";

library.add(fas);

function HomePage() {
  const nextRef = useRef(null);
  const prevRef = useRef(null);

  const [thumbnails, setThumbnails] = useState([ott1, ott2, ott3]);
  const [slides, setSlides] = useState([ott1, ott2, ott3]);
  const [runNextAuto, setRunNextAuto] = useState(null);
  const [runTimeOut, setRunTimeOut] = useState(null);

  const timeRunning = 1000;
  const timeAutoNext = 70000;

  useEffect(() => {
    const nextDom = nextRef.current;
    const prevDom = prevRef.current;

    const showSlider = (type) => {
      if (type === "next") {
        setSlides((prevSlides) => {
          const firstSlide = prevSlides.shift();
          return [...prevSlides, firstSlide];
        });
        setThumbnails((prevThumbnails) => {
          const firstThumbnail = prevThumbnails.shift();
          return [...prevThumbnails, firstThumbnail];
        });
        document.querySelector(".carousel").classList.add("next");
      } else {
        setSlides((prevSlides) => {
          const lastSlide = prevSlides.pop();
          return [lastSlide, ...prevSlides];
        });
        setThumbnails((prevThumbnails) => {
          const lastThumbnail = prevThumbnails.pop();
          return [lastThumbnail, ...prevThumbnails];
        });
        document.querySelector(".carousel").classList.add("prev");
      }

      clearTimeout(runTimeOut);
      setRunTimeOut(
        setTimeout(() => {
          document.querySelector(".carousel").classList.remove("next");
          document.querySelector(".carousel").classList.remove("prev");
        }, timeRunning)
      );

      clearTimeout(runNextAuto);
      setRunNextAuto(
        setTimeout(() => {
          nextDom.click();
        }, timeAutoNext)
      );
    };

    setRunNextAuto(
      setTimeout(() => {
        nextDom.click();
      }, timeAutoNext)
    );

    nextDom.onclick = () => {
      showSlider("next");
    };

    prevDom.onclick = () => {
      showSlider("prev");
    };

    return () => {
      clearTimeout(runNextAuto);
      clearTimeout(runTimeOut);
    };
  }, []);

  return (
    <>
      <div className="carousel">
        <div className="list">
          {slides.map((img, index) => (
            <div className="item" key={index}>
              <img src={img} alt={`Slide ${index + 1}`} />
              <div className="content">
                <div className="title">DESIGN SLIDER</div>
                <div className="topic">ANIMAL</div>
                <div className="des">
                  Lorem ipsum dolor, sit amet consectetur adipisicing elit. Ut
                  sequi, rem magnam nesciunt minima placeat, itaque eum neque
                  officiis unde, eaque optio ratione aliquid assumenda facere ab
                  et quasi ducimus aut doloribus non numquam. Explicabo,
                  laboriosam nisi reprehenderit tempora at laborum natus unde.
                  Ut, exercitationem eum aperiam illo illum laudantium?
                </div>
                <div className="buttons">
                  <button>SEE MORE</button>
                  <button>SUBSCRIBE</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="thumbnail">
          {thumbnails.map((img, index) => (
            <div className="item" key={index}>
              <img src={img} alt={`Thumbnail ${index + 1}`} />
              <div className="content">
                <div className="title">Name Slider</div>
                <div className="description">Description</div>
              </div>
            </div>
          ))}
        </div>

        <div className="arrows">
          <button ref={prevRef}></button>
          <button ref={nextRef}></button>
        </div>
        <div className="time"></div>
      </div>
    </>
  );
}

export default HomePage;
