import React, { useRef, useState, useEffect } from "react";
import { faChevronLeft, faChevronRight } from "@fortawesome/free-solid-svg-icons";
import "../styles/home.css";



import axios from "axios";

import Carousel from "../component/Carousel ";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function HomePage() {


  const [movies, setMovies] = useState([]);
  

  // lấy Api movies
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/movies/");
        setMovies(response.data);
      } catch (error) {
        console.error("Error fetching movies:", error);
      }
    };

    fetchMovies();
  }, []);
  // end Api movies

  return (
    <>
      <div className="container-fluid gx-0 slide-container">
        <div id="slide">
          {
            movies.map((movie,index) =>{
              return <div key={index} className="item" style={{backgroundImage: `url(http://localhost:5000${movie.imgBanner})`}}>
                <h3>{movie.name}</h3>
                <p>{movie.description.substring(0, 100)}...</p>
                <button><a href={movie.videoUrl}>Xem phim</a></button>
              </div>
            })
          }
        </div>
        <div className="buttons">
          <button className="btn btn-previous"><FontAwesomeIcon icon={faChevronRight} /></button>
          <button className="btn btn-next"><FontAwesomeIcon icon={faChevronLeft} /></button>
        </div>
      </div>
      <Carousel />
    </>
  );
}

export default HomePage;
