import React, { useState, useRef, useEffect } from 'react';
import '../styles/Carousel.css';

const slides = [
  { id: 1, image: 'img1.jpg', title: 'Slide 1', description: 'Description 1' },
  { id: 2, image: 'img2.jpg', title: 'Slide 2', description: 'Description 2' },
  { id: 3, image: 'img3.jpg', title: 'Slide 3', description: 'Description 3' },
  { id: 4, image: 'img4.jpg', title: 'Slide 4', description: 'Description 4' },
  { id: 5, image: 'img5.jpg', title: 'Slide 5', description: 'Description 5' },
  { id: 6, image: 'img6.jpg', title: 'Slide 6', description: 'Description 6' },
  { id: 7, image: 'img7.jpg', title: 'Slide 7', description: 'Description 7' },
  { id: 8, image: 'img8.jpg', title: 'Slide 8', description: 'Description 8' },
  { id: 9, image: 'img9.jpg', title: 'Slide 9', description: 'Description 9' },
  { id: 10, image: 'img10.jpg', title: 'Slide 10', description: 'Description 10' },
];

const Carousel = () => {
  const [currentSlides, setCurrentSlides] = useState(slides.slice(0, 5));

  const nextSlide = () => {
    setCurrentSlides((prevSlides) => {
      const firstSlide = prevSlides.shift();
      return [...prevSlides, firstSlide];
    });
  };

  const prevSlide = () => {
    setCurrentSlides((prevSlides) => {
      const lastSlide = prevSlides.pop();
      return [lastSlide, ...prevSlides];
    });
  };

  return (
    <div className="carousel-container">
      <button onClick={prevSlide}>Previous</button>
      <div className="carousel">
        {currentSlides.map((slide, index) => (
          <div className="slide" key={index}>
            <img src={slide.image} alt={slide.title} />
            <div className="content">
              <h2>{slide.title}</h2>
              <p>{slide.description}</p>
            </div>
          </div>
        ))}
      </div>
      <button onClick={nextSlide}>Next</button>
    </div>
  );
};

export default Carousel;
