import React, { useRef, useState, useEffect } from "react";
import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import "../styles/home.css";

import ott1 from "../img/ott1.webp";
import ott2 from "../img/ott2.webp";
import ott3 from "../img/ott3.webp";

library.add(fas);

function HomePage() {
  const nextRef = useRef(null);
  const prevRef = useRef(null);

  const [thumbnails, setThumbnails] = useState([ott2, ott3,ott1]);
  const [slides, setSlides] = useState([ott1, ott2, ott3]);
  const [directorName, setDirectorName] = useState(['Huân Huỳnh', 'Huỳnh Ngọc Huân', 'Ngọc Huân Huỳnh']);
  const [movieName, setMovieName] = useState(['Doraemon', 'pikachu', '7 viên ngọc rồng']);
  const [directorMovie, setMoviedescription] = useState(['Phim hoạt hình Nhật Bản', 'Phim hoạt hình Việt Nam', 'Phim hoạt hình của doraemon']);
  const [runNextAuto, setRunNextAuto] = useState(null);
  const [runTimeOut, setRunTimeOut] = useState(null);

  const timeRunning = 1000;
  const timeAutoNext = 700000; // Changed to a shorter time for testing

  useEffect(() => {
    const nextDom = nextRef.current;
    const prevDom = prevRef.current;

    const showSlider = (type) => {
      if (type === "next") {
        setSlides((prevSlides) => {
          const newSlides = [...prevSlides];
          const firstSlide = newSlides.shift();
          newSlides.push(firstSlide);
          return newSlides;
        });
        setThumbnails((prevThumbnails) => {
          const newThumbnails = [...prevThumbnails];
          const firstThumbnail = newThumbnails.shift();
          newThumbnails.push(firstThumbnail);
          return newThumbnails;
        });
        setMovieName((prevMovieNames) => {
          const newMovieNames = [...prevMovieNames];
          const firstMovieName = newMovieNames.shift();
          newMovieNames.push(firstMovieName);
          return newMovieNames;
        });
        setDirectorName((prevDirectorName) => {
          const newDirectorName = [...prevDirectorName];
          const firstDirectorName = newDirectorName.shift();
          newDirectorName.push(firstDirectorName);
          return newDirectorName;
        });
        setMoviedescription((prevMovieDescriptions) => {
          const newMovieDescriptions = [...prevMovieDescriptions];
          const firstMovieDescription = newMovieDescriptions.shift();
          newMovieDescriptions.push(firstMovieDescription);
          return newMovieDescriptions;
        });
        document.querySelector(".carousel").classList.add("next");
      } else {
        setSlides((prevSlides) => {
          const newSlides = [...prevSlides];
          const lastSlide = newSlides.pop();
          newSlides.unshift(lastSlide);
          return newSlides;
        });
        setThumbnails((prevThumbnails) => {
          const newThumbnails = [...prevThumbnails];
          const lastThumbnail = newThumbnails.pop();
          newThumbnails.unshift(lastThumbnail);
          return newThumbnails;
        });
        setMovieName((prevMovieNames) => {
          const newMovieNames = [...prevMovieNames];
          const lastMovieName = newMovieNames.pop();
          newMovieNames.unshift(lastMovieName);
          return newMovieNames;
        });
        setDirectorName((prevDirectorName) => {
          const newDirectorName = [...prevDirectorName];
          const lastDirectorName = newDirectorName.pop();
          newDirectorName.unshift(lastDirectorName);
          return newDirectorName;
        });
        setMoviedescription((prevMovieDescriptions) => {
          const newMovieDescriptions = [...prevMovieDescriptions];
          const lastMovieDescription = newMovieDescriptions.pop();
          newMovieDescriptions.unshift(lastMovieDescription);
          return newMovieDescriptions;
        });
        document.querySelector(".carousel").classList.add("prev");
      }

      clearTimeout(runTimeOut);
      setRunTimeOut(
        setTimeout(() => {
          document.querySelector(".carousel").classList.remove("next");
          document.querySelector(".carousel").classList.remove("prev");
        }, timeRunning)
      );

      clearTimeout(runNextAuto);
      setRunNextAuto(
        setTimeout(() => {
          nextDom.click();
        }, timeAutoNext)
      );
    };

    setRunNextAuto(
      setTimeout(() => {
        nextDom.click();
      }, timeAutoNext)
    );

    nextDom.onclick = () => {
      showSlider("next");
    };

    prevDom.onclick = () => {
      showSlider("prev");
    };

    return () => {
      clearTimeout(runNextAuto);
      clearTimeout(runTimeOut);
    };
  }, []);

  return (
    <div className="carousel">
      <div className="list">
        {slides.map((img, index) => (
          <div className="item" key={index}>
            <img src={img} alt={`Slide ${index + 1}`} />
            <div className="content">
              <div className="title">{directorMovie[0]}</div>
              <div className="topic">{movieName[0]}</div>
              <div className="des">
                {directorName[0]} - {movieDescription[0]}
              </div>
              <div className="buttons">
                <button>SEE MORE</button>
                <button>SUBSCRIBE</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="thumbnail">
        {thumbnails.map((img, index) => (
          <div className="item" key={index}>
            <img src={img} alt={`Thumbnail ${index + 1}`} />
            <div className="content">
              <div className="title">Name Slider</div>
              <div className="description">Description</div>
            </div>
          </div>
        ))}
      </div>

      <div className="arrows">
        <button ref={prevRef}>Previous</button>
        <button ref={nextRef}>Next</button>
      </div>
      <div className="time"></div>
    </div>
  );
}

export default HomePage;
